import React, { useState } from 'react';

const SynonymsAntonyms = React.memo(props => {
    const { synonyms, antonyms } = props;
    const tabsContent = {};
    let defaultTab;
    if (synonyms) {
        tabsContent.synonyms = synonyms;
        defaultTab = 'synonyms';
    }
    if (antonyms) {
        tabsContent.antonyms = antonyms;
        if (!defaultTab) defaultTab = 'antonyms';
    }
    const [renderTab, updateRenderTab] = useState(tabsContent.synonyms ? 'synonyms' : 'antonyms');
    const renderData = tabsContent[renderTab];

    return (
        <div className="tabs">
            <div className="tabs-header">
                {tabsContent.synonyms && <div onClick={() => updateRenderTab(synonyms)}>Synonyms</div>}
                {tabsContent.antonyms && <div onClick={() => updateRenderTab(antonyms)}>Antonyms</div>}
            </div>
            <div className="tabs-content">
                {renderData && renderData.map(data => {
                    return <a href="https://www.google.com">{data.word}</a>
                })
                }
            </div>
        </div>
    );
});

export default SynonymsAntonyms;
