import React from 'react';
import { useSelector } from 'react-redux';
import Widget from './Widget';

const Widgets = props => {
    const { searchedData } = props;
    const searchState = useSelector(state => state.search);
    const { currentPage = 0 } = searchState;
    console.log(`currentPage ${currentPage}`);
    const renderData = searchedData.slice(currentPage * 5, currentPage * 5 + 5);
    console.log(renderData)
    return (
        <div>
            {
                renderData.map((data, index) => {
                    const { tags, word, defs } = data;
                    return tags.map(tag => {
                        if (tag) {
                            tag = tag[0].toUpperCase();
                        }  
                        const tagText = {
                            N: 'Noun',
                            V: 'Verb',
                            A: 'Adjective'
                        }[tag];
                        return (
                            <Widget key={index} data={{ tag, tagText, word, defs }} />
                        ); 
                    });
                })
            }
        </div>
    );
}

export default Widgets;
