import React from 'react';

const Widgets = props => {
    const { searchedData } = props;
    return (
        <div>
            {
                searchedData.map((data, index) => {
                    const { tags, word } = data;
                    const tag = tags && tags[0] && tags[0].toUpperCase() || '';
                    const tagText = {
                        N: 'Noun',
                        V: 'Verb'
                    }[tag];
                    return (
                        <div className="search-item" key={index}>
                            <div className="item-header display-flex">
                                <div>{tag}</div>
                                <div>
                                    {tagText && <span>{tagText}-</span>}
                                    {word && <span>{word}</span>}
                                </div>
                            </div>
                        </div>
                    );
                })
            }
        </div>
    );
}

export default Widgets;
