import React from 'react';
import { useSelector } from 'react-redux';

const WidgetContainer = () => {
    const searchState = useSelector(state => state.search);
    const { loading, searchedData } = searchState;
    return (
        <div>
            {
                searchedData &&
                <div>
                    {
                        searchedData.map(data => {
                            const { tags, word } = data;
                            const tag = tags && tags[0] && tags[0].toUpperCase() || '';
                            const tagText = {
                                N: 'Noun',
                                V: 'Verb'
                            }[tag];
                            return (
                                <div className="search-item">
                                    <div className="item-header display-flex">
                                        <div>{tag}</div>
                                        <div>
                                            {tagText && <div>{tagText}-</div>}
                                            {word && <div>{word}</div>}
                                        </div>
                                    </div>
                                </div>
                            );
                        })
                    }
                </div>
            }
        </div>
    )
}

export default WidgetContainer;
