import { call, put, takeLatest, all } from 'redux-saga/effects';
import axios from 'axios';
import { types } from '../actions';

const ROOT_URL = 'https://api.datamuse.com/words?md=pd';

export function* fetchDictionaryData({ payload }) {
  try {
    const url = `${ROOT_URL}&sp=${payload}`;
    const response = yield call(axios.get, url);
    yield put({
      type: types.FETCH_DATA_SUCCESS,
      data: response.data
    });
  } catch (error) {
    console.log('Request failed! Could not fetch dictionary data.');
    yield put({
      type: types.FETCH_DATA_FAIL,
      data: error
    });
  }
}

export function* search() {
  yield all([
    takeLatest(types.FETCH_DATA_LOAD, fetchDictionaryData)
  ])
}
