import React from 'react';
import Widget from './Widget';

const Widgets = props => {
    const { searchedData } = props;
    return (
        <div>
            {
                searchedData.map((data, index) => {
                    const { tags, word } = data;
                    const tag = tags && tags[0] && tags[0].toUpperCase() || '';
                    const tagText = {
                        N: 'Noun',
                        V: 'Verb'
                    }[tag];
                    return (
                        <Widget data={{ tag, tagText, word }} />
                    );
                })
            }
        </div>
    );
}

export default Widgets;
