const initialState = {
  loading: '',
  searchedData: null,
  currentPage: 0
};
const search = (prevState = initialState, action) => {
  switch (action.type) {
    case 'FETCH_DATA_LOAD':
      return Object.assign({}, prevState, {
        loading: true
      });
    case 'FETCH_DATA_SUCCESS':
      return Object.assign({}, prevState, {
        loading: false,
        searchedData: action.data,
      });
    case 'UPDATE_CURRENT_PAGE':
      return Object.assign({}, prevState, {
        UPDATE_CURRENT_PAGE
      });
    default:
      return prevState;
  }
}

export default search;
