export const types = {
  FETCH_DATA_LOAD: 'FETCH_DATA_LOAD',
  FETCH_DATA_SUCCESS: 'FETCH_DATA_SUCCESS',
  FETCH_DATA_FAIL: 'FETCH_DATA_FAIL',
  UPDATE_CURRENT_PAGE: 'UPDATE_CURRENT_PAGE',
  FETCH_SYN_ANT_LOAD: 'FETCH_SYN_ANT_LOAD',
  FETCH_SYN_ANT_SUCCESS: 'FETCH_SYN_ANT_SUCCESS',
  FETCH_SYN_ANT_FAIL: 'FETCH_SYN_ANT_FAIL'
};

export function fetchData(query) {
  return { type: 'FETCH_DATA_LOAD', payload: query };
}

export function updateCurrentPage(pageNo) {
  return { type: 'UPDATE_CURRENT_PAGE', pageNo };
}

export function fetchSynAnt(word) {
  return { type: 'FETCH_SYN_ANT_LOAD', word };
}
