import React, { useSelector, useDispatch } from 'react';
import { updateCurrentPage } from '../../actions';
// components
import Search from './Search';
import Pagination from '../Pagination/Pagination';

const SearchContainer = React.memo(() => {
  const searchState = useSelector(state => state.search);
  const { currentPage = 0, searchedData } = searchState;
  const dispatch = useDispatch();
  return (
    <div className="search-container">
      <Search />
      <Pagination current={currentPage} total={Math.ceil(searchedData.length / 5)} navHandler={action => { dispatch(updateCurrentPage(currentPage + action))}} />
    </div>
  );
});

export default SearchContainer;
