import React from 'react';
import Widget from './Widget';

const Widgets = props => {
    const { searchedData } = props;
    return (
        <div>
            {
                searchedData.map((data, index) => {
                    const { tags, word } = data;
                    // eslint-disable-next-line
                    let tag = tags && tags[0] || '';
                    if (tag) {
                        tag = tag[0].toUpperCase();
                    }
                    const tagText = {
                        N: 'Noun',
                        V: 'Verb',
                        A: 'Adjective'
                    }[tag];
                    return (
                        <Widget key={index} data={{ tag, tagText, word }} />
                    );
                })
            }
        </div>
    );
}

export default Widgets;
