import React from 'react';
import './Pagination.css';

const Pagination = React.memo(props => {
    const { current, total, navHandler } = props;
    return (
        <div className="pagination display-flex">
            <span>{current + 1} OUT OF {total} RESULT</span>
            <span className={`cursor-pointer${!current ? ' disabled' : ''}`} onClick={() => navHandler(-1)}>Back</span>
            <span className={`cursor-pointer${current + 1 === total ? ' disabled' : ''}`} onClick={() => navHandler(1)}>Next</span>
        </div>
    );
});

export default Pagination;
