const initialState = {
  loading: '',
  searchedData: null
};
const search = (prevState = initialState, action) => {
  switch (action.type) {
    case 'FETCH_DATA_LOAD':
      return Object.assign({}, prevState, {
        loading: true,
        searchedData: null
      });
    case 'FETCH_DATA_SUCCESS':
      return Object.assign({}, prevState, {
        loading: false,
        searchedData: action.data,
      });
    default:
      return prevState;
  }
}

export default search;
